            const jobDate = new Date(job.deliveryDate);
            const jobDateOnly = new Date(jobDate.getFullYear(), jobDate.getMonth(), jobDate.getDate());
            return jobDateOnly >= yearStart && jobDateOnly <= yearEnd;
          });
          break;

        default:
          filteredJobs = jobs;
