import React, { useState, useEffect, useCallback } from 'react';
import './App.css';

// API Client - Tá»± Ä‘á»™ng detect IP mÃ¡y chá»§ vá»›i fallback
const getApiBase = () => {
  if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
    return 'http://localhost:5001';
  }
  const apiUrl = `http://${window.location.hostname}:5001`;
  console.log('ðŸ” API Base URL:', apiUrl);
  return apiUrl;
};

const getApiUrls = () => {
  const hostname = window.location.hostname;
  const urls = [];
  
  if (hostname === 'localhost' || hostname === '127.0.0.1') {
    urls.push('http://localhost:5001');
    urls.push('http://127.0.0.1:5001');
  } else {
    urls.push(`http://${hostname}:5001`);
    urls.push('http://localhost:5001');
    urls.push('http://127.0.0.1:5001');
  }
  
  return urls;
};

const API_BASE = getApiBase();

const apiClient = {
  async request(endpoint, options = {}) {
    const urlsToTry = getApiUrls();
    
    for (const baseUrl of urlsToTry) {
      try {
        const url = `${baseUrl}${endpoint}`;
        const config = {
          headers: {
            'Content-Type': 'application/json',
            ...options.headers
          },
          ...options
        };

        console.log(`ðŸ” Thá»­ káº¿t ná»‘i Ä‘áº¿n: ${url}`);
        const response = await fetch(url, config);
        const data = await response.json();

        if (!response.ok) {
          throw new Error(data.error || `HTTP ${response.status}`);
        }

        if (baseUrl !== API_BASE) {
          window.API_BASE = baseUrl;
          console.log(`âœ… ÄÃ£ cáº­p nháº­t API_BASE thÃ nh: ${baseUrl}`);
        }

        return data;
      } catch (error) {
        console.log(`âŒ KhÃ´ng thá»ƒ káº¿t ná»‘i Ä‘áº¿n ${baseUrl}:`, error.message);
        continue;
      }
    }
    
    throw new Error('KhÃ´ng thá»ƒ káº¿t ná»‘i Ä‘áº¿n server. Vui lÃ²ng kiá»ƒm tra káº¿t ná»‘i máº¡ng.');
  },

  async login(username, password) {
    try {
      const response = await this.request('/api/login', {
        method: 'POST',
        body: JSON.stringify({ username, password })
      });
      return response;
    } catch (error) {
      return { success: false, error: 'KhÃ´ng thá»ƒ káº¿t ná»‘i server' };
    }
  },

  async getUsers() {
    try {
      const response = await this.request('/api/users');
      return response.data || [];
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y users:', error);
      return [];
    }
  },

  async updateUsers(users) {
    try {
      const response = await this.request('/api/users', {
        method: 'POST',
        body: JSON.stringify({ users })
      });
      return response.success;
    } catch (error) {
      console.error('âŒ Lá»—i khi cáº­p nháº­t users:', error);
      return false;
    }
  },

  async getJobs() {
    try {
      const response = await this.request('/api/jobs');
      return response.data || [];
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y jobs:', error);
      return [];
    }
  },

  async updateJobs(jobs) {
    try {
      const response = await this.request('/api/jobs', {
        method: 'POST',
        body: JSON.stringify({ jobs })
      });
      return response.success;
    } catch (error) {
      console.error('âŒ Lá»—i khi cáº­p nháº­t jobs:', error);
      return false;
    }
  }
};

function App() {
  // Authentication state
  const [currentUser, setCurrentUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loginError, setLoginError] = useState('');
  
  // Network detection state
  const [networkInfo, setNetworkInfo] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('checking');
  
  // Data state
  const [users, setUsers] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  // Network detection functions
  const checkNetworkConnection = useCallback(async () => {
    try {
      setConnectionStatus('checking');
      const urlsToTry = getApiUrls();
      
      for (const url of urlsToTry) {
        try {
          console.log(`ðŸ” Äang thá»­ káº¿t ná»‘i Ä‘áº¿n: ${url}`);
          const response = await fetch(`${url}/api/health`, {
            method: 'GET',
            timeout: 5000
          });
          
          if (response.ok) {
            const data = await response.json();
            setConnectionStatus('connected');
            setNetworkInfo({
              serverUrl: url,
              status: 'connected',
              timestamp: new Date().toISOString()
            });
            
            window.API_BASE = url;
            console.log(`âœ… Káº¿t ná»‘i thÃ nh cÃ´ng Ä‘áº¿n: ${url}`);
            return;
          }
        } catch (error) {
          console.log(`âŒ KhÃ´ng thá»ƒ káº¿t ná»‘i Ä‘áº¿n ${url}:`, error.message);
          continue;
        }
      }
      
      setConnectionStatus('disconnected');
      setNetworkInfo({
        serverUrl: null,
        status: 'disconnected',
        timestamp: new Date().toISOString()
      });
    } catch (error) {
      console.error('âŒ Lá»—i kiá»ƒm tra káº¿t ná»‘i:', error);
      setConnectionStatus('error');
    }
  }, []);

  // Load data on mount
  useEffect(() => {
    const loadData = async () => {
      setIsLoading(true);
      try {
        const [usersData, jobsData] = await Promise.all([
          apiClient.getUsers(),
          apiClient.getJobs()
        ]);
        
        setUsers(usersData);
        setJobs(jobsData);
        setError('');
      } catch (error) {
        console.error('âŒ Lá»—i khi táº£i dá»¯ liá»‡u:', error);
        setError('KhÃ´ng thá»ƒ táº£i dá»¯ liá»‡u tá»« server');
      } finally {
        setIsLoading(false);
      }
    };

    if (isAuthenticated) {
      loadData();
    }
  }, [isAuthenticated]);

